import React from 'react'
import boxes from './boxes'
import Box from './Box';
import './styles.css'; 

export default function App(props) {

    const [squares, setSquares] = React.useState(boxes)

    function toggle(id){
        console.log(id)
    }

    const squareElements = squares.map(square => (
        <Box 
        key={square.id} 
        id={square.id}
        on={square.on} 
        toggle={toggle}
        />
    ))

    return (
    <main>
        {squareElements}
    </main>
  )
}
